const items = $input.all();
const input_json = items[0].json;

// 検索クエリ
const queries = input_json.search_queries;
const search_goals = input_json.search_goals;

if (queries.length !== search_goals.length) {
  // エラーもn8n形式で返すと扱いやすいです
  return [{
    json: { error: "The query length and goal length must be same" }
  }];
}

const search_topics = queries.map((q, i) => {
  return {
    search_goal: search_goals[i],
    search_query: q
  }
})

// ユーザーの質問
const context = input_json.user_query;
const fetch = require('node-fetch');

// 共有DBのID
const shared_db_id = input_json.shared_db_id

// サブエージェントを呼び出す非同期関数
const searchRequest =  async function(topic) {
  const url = 'http://localhost:5678/webhook/a839d203-3491-4ee5-9d78-325e98e9db26';
  try {
    const response = await fetch(url, {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ 
        search_topic: topic.search_query,
        search_goal: topic.search_goal,
        user_query: context,
        shared_db_id: shared_db_id
      })
    });

    if (!response.ok) throw new Error(`HTTP error! status: ${response.status}`);
    const data = await response.json();

    return { 
      status: 'fulfilled', 
      search_topic: topic.search_query, 
      search_goal: topic.search_goal,
      data: data
    };
  } catch (error) {
    // エラーでも止まらないようにエラー情報を返す
    return { 
      status: 'rejected', 
      search_topic: topic.search_query, 
      search_goal: topic.search_goal,
      error: error.message 
    };
  }
};

// 全クエリを並列実行し、全ての結果が返るのを待つ
const promises = search_topics.map(t => searchRequest(t));
const results = await Promise.all(promises);

// 成功した結果だけをフィルタリングして返す
const successResults = results.filter(r => r.status === 'fulfilled').map((r) => {
  return {
    search_topic: r.search_topic,
    search_goal: r.search_goal,
    result: r.data.output
  }
});

return successResults

